import * as types from '../constants/actionTypes'
import firebase from '../constants/appconfig';

export function AddData(params) {
    var arr = params;
    return (dispatch) => {
        for (var k in arr) {
            firebase.database().ref('users/' + arr[k].key).set({
                name: arr[k].value.name,
                account: arr[k].value.account,
            }).then(function () {
                console.log('Data added succesfully');
            }).catch(function (error) {
                console.log('Synchronization failed', error);
            });
        }
    }
}
export function fetchData(params) {
    return (dispatch) => {
        const db = firebase.database().ref('users');
        db.on("value", function (snapshot) {
            let newUserState = [];
            snapshot.forEach(data => {
                const dataVal = data.val()
                newUserState.push({
                    id: data.key,
                    name: dataVal.name,
                    account: dataVal.account
                })
            })
            dispatch({ type: types.RESPONSE_FETCH_USER, data: newUserState });
        }, function (errorObject) {
            console.log("The read failed: " + errorObject.code);
        });
    }
}
