import { Component } from 'react';
import data from './../data.json';
import { Table, Button } from 'react-bootstrap'
import { connect } from 'react-redux';
import { Spin, Space } from 'antd';
import { AddData, fetchData } from './../Redux/actions/useraction'
import { bindActionCreators } from 'redux'

class UserList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            newData: []
        }
    }

    addData() {
        var arr = [];
        for (var i in Object.keys(data.users)) {
            var key = Object.keys(data.users)[i]
            var values = Object.values(data.users)[i]
            arr.push({
                key: key,
                value: values
            })
        }
        this.setState({ newData: arr })
        this.props.AddData(arr)
    }
    componentDidMount() {
        this.props.fetchData();
    }
    render() {
        console.log("this.props", this.props)
        let userData = []
        let ele = null;
        this.props.userList.map((data) => {
            const { id, account, name } = data;
            ele = (<tr key={id}>
                <td>
                    {id}
                </td>
                <td>
                    {name}
                </td>
                <td>
                    {account}
                </td>
            </tr>)
            return userData.push(ele);
        })
        console.log("userData", userData)
        return (
            <div>
                <div>
                    <h1>Data fetched from Firebase Real Time Database</h1>
                    <Button onClick={() => { this.addData() }}>
                        Add Data
                    </Button>
                </div>
                {userData && userData.length !== 0 ?
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>User ID</th>
                                <th>Name</th>
                                <th>Account</th>
                            </tr>
                        </thead>
                        <tbody>
                            {userData}
                        </tbody>
                    </Table> : <div><h4>No Data Found</h4></div>}
            </div>
        )
    }
}
function mapStateToProps(state) {
    return {
        user: state.user,
        userList: state.user.userList,
    }
}
function mapDispatchToProps(dispatch) {
    return bindActionCreators(
        {
            AddData,
            fetchData
        },
        dispatch
    )
}
export default connect(mapStateToProps, mapDispatchToProps)(UserList)
