import { Component } from 'react';
import data from './../data.json';
import { Table, Button } from 'react-bootstrap'
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { Spin } from 'antd';
import { AddData, fetchData,fetchAccounts } from './../Redux/actions/useraction'
import { bindActionCreators } from 'redux'

class AppList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            newData: [],
            dataload: this.props.msg === "" ? 'fetching' : 'fetched'
        }
    }

    UpdateRatings() {
        this.props.UpdateRatings(data)
    }
    componentDidMount() {
        this.props.fetchAccounts();
    }
    render() {
        console.log("this.props",this.props)
        let AppData = []
        let ele = null;
        this.props.AccountList.map((data) => {
            const { id, title, name } = data;
            ele = (<tr key={id}>
                <td>{id}</td>
                <td>{name}</td>
                <td>{title}</td>
            </tr>)
            return AppData.push(ele);
        })
        return (
            <div>
                <div>
                    <h1>Data {this.props.msg === "" ? 'fetching' : 'fetched'} from Firebase Real Time Database</h1>
                </div>
                <Link className="btn info" to="Apps">Go TO Apps</Link>
                {AppData && AppData.length !== 0 ?
                    <div>
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>App ID</th>
                                    <th>Name</th>
                                    <th>Title</th>
                                    <th>Ratings</th>
                                </tr>
                            </thead>
                            <tbody>
                                {AppData}
                            </tbody>
                        </Table>
                    </div> : <div> <h4>{this.props.msg}</h4>{this.props.msg === "" ? <Spin tip="Loading..."></Spin> : this.props.msg === "No Data" && <Button onClick={() => { this.addData() }}>
                        Add data from Data.json file
                    </Button>}
                    </div>}
            </div>
        )
    }
}
function mapStateToProps(state) {
    return {
        user: state.user,
        AccountList: state.user.AccountList,
        msg: state.user.msg
    }
}
function mapDispatchToProps(dispatch) {
    return bindActionCreators(
        {
            AddData,
            fetchData,
            fetchAccounts
        },
        dispatch
    )
}
export default connect(mapStateToProps, mapDispatchToProps)(AppList)
