        db.on('value', function (snapshot) {
            var userId = snapshot.val().account;
            snapshot.forEach(data => {
                const dataVal = data.val()
                db1.child(dataVal.account).once('value', function (mediaSnap) {
                    mediaSnap.forEach(data => {
                        const dataVal = data.val();
                        dataVal.forEach(title=>{
                            const title1 = title.val();
